import React, { FormEvent, useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// CSS
import './Profile.css';

// Toatfy
import { toast } from 'react-toastify';

// Layouts
import Header from "../../Layouts/Header";
import Title from "../../Layouts/Title";
import Input from "../../Layouts/Input";

// Icons
import { FiSettings, FiUpload } from "react-icons/fi";

// API
import { api } from "../../../utils/api";

// Context
import { AuthContext } from "../../../Contexts/auth";

const Profile = () => {
    const { company } = useContext<any>(AuthContext);
    const navigate = useNavigate();
    const [companyEdit, setCompanyEdit] = useState<any>({});

    useEffect(() => {
        api.get('company/edit', {
            headers: {
                session: JSON.parse(localStorage.getItem('@session')!),
                email: company.email
            }
        })
        .then((res : any) => {
            setCompanyEdit(res.data);
        })
    }, []);

    const handleOnChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setCompanyEdit({...companyEdit, [e.target.name]: e.target.value});
    }

    const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();

    }

    const handleUpload = async () => {

    }

    return (
        <div>
            <Header />
            <div className="content">
                <Title name='Minha conta'> 
                    <FiSettings size={25}/>
                </Title>

                {companyEdit && (
                    <div className="container">
                        <form className="form-profile" onSubmit={handleSubmit}>
                            <Input 
                                type="text" 
                                name="name" 
                                placeholder="Seu nome" 
                                value={companyEdit.name? companyEdit.name : ''} 
                                text="Nome"
                                handleOnChange={handleOnChange}
                            />

<Input 
                                type="text" 
                                name="name" 
                                placeholder="Seu nome" 
                                value={companyEdit.name? companyEdit.name : ''} 
                                text="Nome"
                                handleOnChange={handleOnChange}
                            />

                            <button type="submit">Salvar</button>

                        </form>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Profile;
